-- ----------------------------
-- Table structure for application
-- ----------------------------
DROP TABLE IF EXISTS `application`;
CREATE TABLE `application` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `authority_id` int(11) DEFAULT NULL,
  `donee_id` int(11) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `description` longtext,
  `category_id` int(11) DEFAULT NULL,
  `img` varchar(255) DEFAULT NULL,
  `target` double(11,2) DEFAULT NULL,
  `donoor_num` int(11) DEFAULT NULL,
  `start` datetime DEFAULT NULL,
  `end` datetime DEFAULT NULL,
  `status` int(1) DEFAULT '0' COMMENT '待审核0，未开始1，进行中,2，待处理3，已完成4，未通过5',
  `urgent` int(11) DEFAULT '0',
  `receive` double NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `FK_applyment_category_id` (`category_id`),
  KEY `FK_applyment_donere_id` (`donee_id`),
  CONSTRAINT `FK_applyment_category_id` FOREIGN KEY (`category_id`) REFERENCES `category` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `FK_applyment_donere_id` FOREIGN KEY (`donee_id`) REFERENCES `donee` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb4;

