-- ----------------------------
-- Table structure for donate
-- ----------------------------
DROP TABLE IF EXISTS `donate`;
CREATE TABLE `donate` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `donor_id` int(11) DEFAULT NULL,
  `application_id` int(11) DEFAULT NULL,
  `money` double(11,2) DEFAULT NULL,
  `is_anoymous` int(11) NOT NULL,
  `message` varchar(255) DEFAULT NULL,
  `prove` varchar(255) NOT NULL COMMENT '捐赠证明',
  `date` datetime DEFAULT NULL,
  `status` int(11) DEFAULT NULL,
  `authority_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_donate_donor_id` (`donor_id`) USING BTREE,
  KEY `FK_donate_authority_id` (`authority_id`),
  KEY `UK_donor_id_application_id` (`application_id`,`donor_id`) USING BTREE,
  CONSTRAINT `FK_donate_applyment_id` FOREIGN KEY (`application_id`) REFERENCES `application` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `FK_donate_authority_id` FOREIGN KEY (`authority_id`) REFERENCES `authority` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `FK_donate_donor_id` FOREIGN KEY (`donor_id`) REFERENCES `donor` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8mb4;

