-- ----------------------------
-- Table structure for trace
-- ----------------------------
DROP TABLE IF EXISTS `trace`;
CREATE TABLE `trace` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `applyment_id` int(11) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `content` longtext,
  `time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_trace_authority_id` (`applyment_id`),
  CONSTRAINT `FK_trace_authority_id` FOREIGN KEY (`applyment_id`) REFERENCES `application` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8mb4;

