mapper.select:
select
  application.id, application.authority_id,authority.user_id as authority_user_id,donee_id,donee.user_id as donee_user_id,title, application.description, category_id, img, target, donoor_num, start, end, application.status, urgent, receive
from charity.application,authority,donee

    application.authority_id = authority.id and(application.donee_id = donee.id or donee_id is null)

        and id = #{id}


        and authority_id = #{authorityId}


        and donee_id = #{doneeId}


        and title = #{title}


        and description = #{description}


        and category_id = #{categoryId}


        and img = #{img}


        and target = #{target}


        and donoor_num = #{donoorNum}


        and start = #{start}


        and end = #{end}


        and status = #{status}


        and urgent = #{urgent}


        and receive = #{receive}

select count(*) from application


    and id = #{id}


    and authority_id = #{authorityId}


    and donee_id = #{doneeId}


    and title = #{title}


    and description = #{description}


    and category_id = #{categoryId}


    and img = #{img}


    and target = #{target}


    and donoor_num = #{donoorNum}


    and start = #{start}


    and end = #{end}


    and status = #{status}


    and urgent = #{urgent}


    and receive = #{receive}
