mapper.select:
select
  authority.id, name, last_login,user_id, found, people, description, tel, address, represent, created_time, status
from charity.authority,charity.user

    authority.user_id = user.id

        and id = #{id}


        and user_id = #{userId}


        and found = #{found}


        and people = #{people}


        and description = #{description}


        and tel = #{tel}


        and address = #{address}


        and represent = #{represent}


        and created_time = #{createdTime}


        and status = #{status}
