mapper.select:
select
  id, donor_id, application_id, money, is_anoymous, message, prove, date, status
from charity.donate


        and id = #{id}


        and donor_id = #{donorId}


        and application_id = #{applicationId}


        and money = #{money}


        and is_anoymous = #{isAnoymous}


        and message = #{message}


        and prove = #{prove}


        and date = #{date}


        and status = #{status}

select
id, donor_id, application_id, money, is_anoymous, message, prove, date, status
from charity.donate where donor_id = #{donorId}

select coalesce(sum(money),0) from donate


    and donor_id = #{donate.donorId}


    and application_id = #{donate.applicationId}


    and date >= #{startDate}


    and date <= #{startDate}

select coalesce(sum(money),0) as money,DATE(date) as date from donate

    DATE_SUB(CURDATE(), INTERVAL 7 DAY) <= date(date)

        and donor_id = #{donorId}


        and application_id = #{applicationId}


group by date
order by date desc
